package com.example.airline.Entity;import jakarta.persistence.*;import lombok.Data;import java.time.LocalDateTime;@Data@Entitypublic class Booking {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    private User user;    @ManyToOne    private Flight flight;    private String seatNumber;    private LocalDateTime bookingTime;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }    public Flight getFlight() {        return flight;    }    public void setFlight(Flight flight) {        this.flight = flight;    }    public String getSeatNumber() {        return seatNumber;    }    public void setSeatNumber(String seatNumber) {        this.seatNumber = seatNumber;    }    public LocalDateTime getBookingTime() {        return bookingTime;    }    public void setBookingTime(LocalDateTime bookingTime) {        this.bookingTime = bookingTime;    }}