package com.example.airline.Entity;import jakarta.persistence.*;import lombok.Data;import java.time.LocalDate;import java.time.LocalDateTime;import java.time.LocalTime;import java.util.List;@Data@Entitypublic class Flight {    public String getSource() {        return source;    }    public void setSource(String source) {        this.source = source;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getAirline() {        return airline;    }    public void setAirline(String airline) {        this.airline = airline;    }    public String getDestination() {        return destination;    }    public List<Booking> getBookings() {        return bookings;    }    public void setBookings(List<Booking> bookings) {        this.bookings = bookings;    }    public void setDestination(String destination) {        this.destination = destination;    }    public int getTotalSeats() {        return totalSeats;    }    public void setTotalSeats(int totalSeats) {        this.totalSeats = totalSeats;    }    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String airline;    private String source;    private String destination;    private LocalDateTime departureTime;    public LocalDateTime getDepartureTime() {        return departureTime;    }    public void setDepartureTime(LocalDateTime departureTime) {        this.departureTime = departureTime;    }    private int totalSeats;    @OneToMany(mappedBy = "flight")    private List<Booking> bookings;}