package com.example.airline.Repositery;import com.example.airline.Entity.Booking;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface BookingRepositroy extends JpaRepository<Booking, Long> {    List<Booking> findByFlightId(Long flightId);    List<Booking> findByUserId(Long userId);}