package com.example.airline.Service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.mail.MailParseException;import org.springframework.mail.MailSendException;import org.springframework.mail.SimpleMailMessage;import org.springframework.mail.javamail.JavaMailSender;import org.springframework.stereotype.Service;@Servicepublic class EmailService {   @Autowired    private JavaMailSender javaMailSender;    public void sendMail(String to,String subject,String body){        try{        SimpleMailMessage mail= new SimpleMailMessage();        mail.setTo(to);        mail.setSubject(subject);        mail.setText(body);        javaMailSender.send(mail);    }        catch (MailParseException e){            System.out.println("error while Parsing email"+e.getMessage());        }    catch (MailSendException e){            System.out.println("error while sending email"+e.getMessage());        }        }}