package com.example.airline.dto;import lombok.Data;import java.time.LocalDateTime;//@Datapublic class BookingRequestDTO {    private Long userId;    private Long flightId;    private String seatNumber;    private LocalDateTime bookingTime;    public Long getUserId() {        return userId;    }    public void setUserId(Long userId) {        this.userId = userId;    }    public Long getFlightId() {        return flightId;    }    public void setFlightId(Long flightId) {        this.flightId = flightId;    }    public String getSeatNumber() {        return seatNumber;    }    public void setSeatNumber(String seatNumber) {        this.seatNumber = seatNumber;    }    public LocalDateTime getBookingTime() {        return bookingTime;    }    public void setBookingTime(LocalDateTime bookingTime) {        this.bookingTime = bookingTime;    }}