package com.example.airline.dto;import lombok.Data;import java.time.LocalDate;import java.time.LocalTime;@Datapublic class FlightRequestDTO {    private String airline;    private String source;    private String destination;    private LocalDate departureDate;    private LocalTime departureTime;    private int totalSeats;    public String getAirline() {        return airline;    }    public void setAirline(String airline) {        this.airline = airline;    }    public String getSource() {        return source;    }    public void setSource(String source) {        this.source = source;    }    public String getDestination() {        return destination;    }    public void setDestination(String destination) {        this.destination = destination;    }    public LocalDate getDepartureDate() {        return departureDate;    }    public void setDepartureDate(LocalDate departureDate) {        this.departureDate = departureDate;    }    public LocalTime getDepartureTime() {        return departureTime;    }    public void setDepartureTime(LocalTime departureTime) {        this.departureTime = departureTime;    }    public int getTotalSeats() {        return totalSeats;    }    public void setTotalSeats(int totalSeats) {        this.totalSeats = totalSeats;    }}