package com.example.airline.dto;import lombok.Data;import java.time.LocalDateTime;import java.time.LocalTime;@Datapublic class FlightRequestDTO {    private String airline;    public String getSource() {        return source;    }    public void setSource(String source) {        this.source = source;    }    public String getAirline() {        return airline;    }    public void setAirline(String airline) {        this.airline = airline;    }    public String getDestination() {        return destination;    }    public void setDestination(String destination) {        this.destination = destination;    }    public LocalDateTime getDepartureTime() {        return departureTime;    }    public void setDepartureTime(LocalDateTime departureTime) {        this.departureTime = departureTime;    }    public int getTotalSeats() {        return totalSeats;    }    public void setTotalSeats(int totalSeats) {        this.totalSeats = totalSeats;    }    private String source;    private String destination;    private LocalDateTime departureTime;    private int totalSeats;}